import React from 'react';
import './AITestingTools.css';

// 使用内联SVG作为默认图标
const defaultIcon = (
  <svg width="150" height="150" viewBox="0 0 150 150" fill="none" xmlns="http://www.w3.org/2000/svg">
    <rect width="150" height="150" fill="#E5E5E5"/>
    <path d="M75 40L95.7128 75.2564L75 110.513L54.2872 75.2564L75 40Z" stroke="#666666" strokeWidth="2"/>
    <circle cx="75" cy="75" r="35" stroke="#666666" strokeWidth="2"/>
  </svg>
);

const aiTestingTools = [
  { name: 'Testim', description: 'AI驱动的端到端测试自动化平台。', link: 'https://www.testim.io/' },
  { name: 'Functionize', description: '使用AI和ML进行智能测试自动化。', link: 'https://www.functionize.com/' },
  { name: 'Applitools', description: '基于AI的视觉测试和监控平台。', link: 'https://applitools.com/' },
  { name: 'Mabl', description: '智能测试自动化平台。', link: 'https://www.mabl.com/' },
  { name: 'Eggplant', description: '智能自动化测试和监控解决方案。', link: 'https://www.eggplantsoftware.com/' },
  { name: 'Sauce Labs', description: '云端持续测试平台，支持AI驱动的测试。', link: 'https://saucelabs.com/' },
  { name: 'Perfecto', description: '智能测试自动化平台，支持Web和移动应用。', link: 'https://www.perfecto.io/' },
  { name: 'TestCraft', description: '基于AI的无代码测试自动化平台。', link: 'https://www.testcraft.io/' },
  { name: 'ReTest', description: '使用AI进行差异测试的工具。', link: 'https://retest.de/en/' },
  { name: 'Mesmer', description: 'AI驱动的移动应用测试自动化平台。', link: 'https://www.mesmerhq.com/' },
  { name: 'Testleft', description: 'AI增强的测试自动化工具，集成于开发环境。', link: 'https://smartbear.com/product/testleft/' },
  { name: 'Appsurify', description: '使用AI优化测试套件的工具。', link: 'https://www.appsurify.com/' },
  { name: 'Parasoft', description: '提供AI驱动的测试自动化解决方案。', link: 'https://www.parasoft.com/' },
  { name: 'Diffblue', description: '使用AI自动生成Java单元测试。', link: 'https://www.diffblue.com/' },
  { name: 'Kobiton', description: 'AI驱动的移动应用测试平台。', link: 'https://www.kobiton.com/' },
  { name: 'Testbirds', description: '众包和AI驱动的软件测试平台。', link: 'https://www.testbirds.com/' },
  { name: 'Autify', description: 'AI驱动的无代码Web应用测试自动化平台。', link: 'https://autify.com/' },
];

function AITestingTools() {
  return (
    <div className="ai-testing-tools">
      <h2>AI 测试工具合集</h2>
      <div className="tool-grid">
        {aiTestingTools.map((tool, index) => (
          <a href={tool.link} key={index} className="tool-card-link" target="_blank" rel="noopener noreferrer">
            <div className="tool-card">
              <div className="tool-logo">
                {defaultIcon}
              </div>
              <h3>{tool.name}</h3>
              <p>{tool.description}</p>
            </div>
          </a>
        ))}
      </div>
    </div>
  );
}

export default AITestingTools;
