// src/components/TestingProjects.js
import React from 'react';
import './TestingProjects.css';

const testingProjects = [
  {
    id: 1,
    title: "电商网站全流程测试",
    description: "对某大型电商平台进行全面的功能测试、性能测试和安全测试，包括用户注册、登录、搜索、下单、支付等核心功能。",
    tools: ["Selenium", "JMeter", "OWASP ZAP"],
    duration: "8周",
    team: "5人团队"
  },
  {
    id: 2,
    title: "移动银行APP测试",
    description: "针对某银行新版移动APP进行全面测试，包括功能测试、兼容性测试、安全测试和用户体验测试。",
    tools: ["Appium", "TestComplete", "Burp Suite"],
    duration: "12周",
    team: "7人团队"
  },
  {
    id: 3,
    title: "企业级CRM系统测试",
    description: "对大型企业定制的CRM系统进行全面测试，包括单元测试、集成测试、系统测试和验收测试。",
    tools: ["JUnit", "Selenium", "LoadRunner"],
    duration: "16周",
    team: "10人团队"
  },
  {
    id: 4,
    title: "物联网设备控制系统测试",
    description: "针对智能家居控制系统进行全面测试，包括功能测试、性能测试、安全测试和兼容性测试。",
    tools: ["Robot Framework", "Gatling", "Wireshark"],
    duration: "10周",
    team: "6人团队"
  },
  {
    id: 5,
    title: "在线教育平台测试",
    description: "对某在线教育平台进行全面测试，包括功能测试、性能测试、安全测试和可访问性测试。",
    tools: ["TestNG", "Apache JMeter", "WAVE"],
    duration: "14周",
    team: "8人团队"
  }
];

function TestingProjects() {
  return (
    <div className="testing-projects">
      <h2>软件测试项目实战</h2>
      <div className="project-list">
        {testingProjects.map((project) => (
          <div key={project.id} className="project-card">
            <h3>{project.title}</h3>
            <p>{project.description}</p>
            <div className="project-details">
              <span><strong>使用工具：</strong> {project.tools.join(', ')}</span>
              <span><strong>项目周期：</strong> {project.duration}</span>
              <span><strong>团队规模：</strong> {project.team}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TestingProjects;
