import React, { useState } from 'react';
import './ModalLogin.css';
import { WechatOutlined, CloseOutlined } from '@ant-design/icons';
import axios from 'axios';

const ModalLogin = ({ onClose }) => {
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/register', { phone, password });
      setMessage(response.data.message);
      setMessageType('success');
      // Clear form fields after successful registration
      setPhone('');
      setPassword('');
    } catch (error) {
      setMessage(error.response?.data?.message || '注册失败，请稍后再试');
      setMessageType('error');
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>
          <CloseOutlined />
        </button>
        <div className="left-panel">
          <h2>欢迎来到mikechen</h2>
          <ul>
            <li>10年+大厂架构经验分享</li>
            <li>全面深入50W+年薪技能</li>
            <li>构建体系化的架构师能力</li>
            <li>架构面试干货，每日更新</li>
            <li>大厂面试经验，定期分享</li>
          </ul>
          <div className="decoration"></div>
        </div>
        <div className="right-panel">
          <h2>mikechen</h2>
          <p className="countdown">3秒登录即可学习</p>
          <form onSubmit={handleRegister}>
            <input 
              type="text" 
              name="phone" 
              placeholder="登录手机号" 
              required 
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
            <input 
              type="password" 
              name="password" 
              placeholder="密码" 
              required 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button type="submit">快速注册</button>
          </form>
          {message && <p className={`message ${messageType}`}>{message}</p>}
          <div className="login-options">
            <a href="#">忘记密码?</a>
            <a href="#">已有账户? 登录</a>
          </div>
          <div className="wechat-login">
            <span>推荐登录:</span>
            <button className="wechat-button"><WechatOutlined /> 微信</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ModalLogin;
