import React, { useState } from 'react';
import './ModalLogin.css';
import { CloseOutlined, WechatOutlined } from '@ant-design/icons';

const ModalLogin = ({ onClose }) => {
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // 处理登录逻辑
    console.log('Login attempted with:', phone, password);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>
          <CloseOutlined />
        </button>
        <div className="left-panel">
          <h2>欢迎来到mikechen</h2>
          <ul>
            <li>10年+大厂架构经验分享</li>
            <li>全面深入50W+年薪技能</li>
            <li>构建体系化的架构师能力</li>
            <li>架构面试干货，每日更新</li>
            <li>大厂面试经验，定期分享</li>
          </ul>
        </div>
        <div className="right-panel">
          <h2>mikechen</h2>
          <p className="login-prompt">3秒登录即可学习</p>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="登录手机号"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
            <input
              type="password"
              placeholder="密码"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button type="submit">快速登录</button>
          </form>
          <div className="login-options">
            <a href="#">忘记密码？</a>
            <a href="#">新用户注册</a>
          </div>
          <div className="wechat-login">
            <span>推荐登录：</span>
            <button className="wechat-button">
              <WechatOutlined /> 微信
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ModalLogin;
