import React from 'react';
import './AITestingTools.css';

const aiTestingTools = [
  { name: 'Testim', description: 'AI驱动的端到端测试自动化平台。', link: 'https://www.testim.io/', image: 'https://via.placeholder.com/150?text=Testim' },
  { name: 'Functionize', description: '使用AI和ML进行智能测试自动化。', link: 'https://www.functionize.com/', image: 'https://via.placeholder.com/150?text=Functionize' },
  { name: 'Applitools', description: '基于AI的视觉测试和监控平台。', link: 'https://applitools.com/', image: 'https://via.placeholder.com/150?text=Applitools' },
  { name: 'Mabl', description: '智能测试自动化平台。', link: 'https://www.mabl.com/', image: 'https://via.placeholder.com/150?text=Mabl' },
  { name: 'Eggplant', description: '智能自动化测试和监控解决方案。', link: 'https://www.eggplantsoftware.com/', image: 'https://via.placeholder.com/150?text=Eggplant' },
  { name: 'Sauce Labs', description: '云端持续测试平台，支持AI驱动的测试。', link: 'https://saucelabs.com/', image: 'https://via.placeholder.com/150?text=Sauce+Labs' },
  { name: 'Perfecto', description: '智能测试自动化平台，支持Web和移动应用。', link: 'https://www.perfecto.io/', image: 'https://via.placeholder.com/150?text=Perfecto' },
  { name: 'TestCraft', description: '基于AI的无代码测试自动化平台。', link: 'https://www.testcraft.io/', image: 'https://via.placeholder.com/150?text=TestCraft' },
  { name: 'ReTest', description: '使用AI进行差异测试的工具。', link: 'https://retest.de/en/', image: 'https://via.placeholder.com/150?text=ReTest' },
  { name: 'Mesmer', description: 'AI驱动的移动应用测试自动化平台。', link: 'https://www.mesmerhq.com/', image: 'https://via.placeholder.com/150?text=Mesmer' },
  { name: 'Testleft', description: 'AI增强的测试自动化工具，集成于开发环境。', link: 'https://smartbear.com/product/testleft/', image: 'https://via.placeholder.com/150?text=Testleft' },
  { name: 'Appsurify', description: '使用AI优化测试套件的工具。', link: 'https://www.appsurify.com/', image: 'https://via.placeholder.com/150?text=Appsurify' },
  { name: 'Parasoft', description: '提供AI驱动的测试自动化解决方案。', link: 'https://www.parasoft.com/', image: 'https://via.placeholder.com/150?text=Parasoft' },
  { name: 'Diffblue', description: '使用AI自动生成Java单元测试。', link: 'https://www.diffblue.com/', image: 'https://via.placeholder.com/150?text=Diffblue' },
  { name: 'Kobiton', description: 'AI驱动的移动应用测试平台。', link: 'https://www.kobiton.com/', image: 'https://via.placeholder.com/150?text=Kobiton' },
  { name: 'Testbirds', description: '众包和AI驱动的软件测试平台。', link: 'https://www.testbirds.com/', image: 'https://via.placeholder.com/150?text=Testbirds' },
  { name: 'Autify', description: 'AI驱动的无代码Web应用测试自动化平台。', link: 'https://autify.com/', image: 'https://via.placeholder.com/150?text=Autify' },
];

function AITestingTools() {
  return (
    <div className="ai-testing-tools">
      <h2>AI 测试工具合集</h2>
      <div className="tool-grid">
        {aiTestingTools.map((tool, index) => (
          <a href={tool.link} key={index} className="tool-card-link" target="_blank" rel="noopener noreferrer">
            <div className="tool-card">
              <img 
                src={tool.image} 
                alt={tool.name} 
                className="tool-logo" 
              />
              <h3>{tool.name}</h3>
              <p>{tool.description}</p>
            </div>
          </a>
        ))}
      </div>
    </div>
  );
}

export default AITestingTools;
