// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // 确保导入 Switch
import Header from './components/Header';
import MainSection from './components/MainSection';
import Footer from './components/Footer';
import Login from './components/Login';
import './App.css'; // 引入 CSS 文件


function App() {
  return (
    <Router>
      <div className="App">
        <Header /> {/* 页头 */}
        <Routes>
          <Route path="/login" element={<Login />} /> {/* 更新为 element 属性 */}
        <Route path="/" element={<MainSection />} /> {/* 默认路由 */}
        </Routes>
        <Footer /> {/* 页脚 */}
      </div>
    </Router>
  );
}

export default App;
