// src/components/Header.js
import React,{useState} from 'react';
import { Link } from 'react-router-dom';
import ModalLogin from './ModalLogin';

function Header() {
  const [isModalOpen, setModalOpen] = useState(false);

  const handleLoginClick = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };
  return (
    <header>
      <h2 className="logo">kristenwang</h2>
      <nav>
        <ul>
          <li>
            <Link to="/topics">AI赋能</Link> {/* 点击时跳转到 /topics */}
          </li>
          <li>
            <Link to="/projects">项目实战</Link>
          </li>
          <li>
            <Link to="/interviews">大厂面试</Link>
          </li>
          <li>
            <Link to="/about">关于</Link>
          </li>
        </ul>
      </nav>
      <button className="login-btn" onClick={handleLoginClick}>登录</button>

      {isModalOpen && <ModalLogin onClose={closeModal} />}
    </header>
  );
}

export default Header;

