// ModalLogin.js
import React, { useState, useEffect } from 'react';
import './ModalLogin.css';
import { WechatOutlined } from '@ant-design/icons';

const ModalLogin = ({ onClose }) => {
    console.log('ModalLogin component is rendering');
    const [countdown, setCountdown] = useState(3);

  useEffect(() => {
    const timer = setInterval(() => {
      setCountdown((prevCount) => (prevCount > 0 ? prevCount - 1 : 0));
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>✖</button>
        <div className="left-panel">
          <h2>欢迎来到kristenwang</h2>
          <ul>
            <li>8年+大厂架构经验分享</li>
            <li>全面深入50W+年薪技能</li>
            <li>AI赋能软件测试能力</li>
            <li>架构面试干货，每日更新</li>
            <li>大厂面试经验，定期分享</li>
          </ul>
          <div className="decoration"></div>
        </div>
        <div className="right-panel">
          <h2>mikechen</h2>
          <p className="countdown">{countdown}秒登录即可学习</p>
          <form>
            <input type="text" name="phone" placeholder="登录手机号" required />
            <input type="password" name="password" placeholder="密码" required />
            <button type="submit">快速登录</button>
          </form>
          <div className="login-options">
            <a href="#">忘记密码?</a>
            <a href="#">新用户？注册</a>
          </div>
          <div className="wechat-login">
            <span>推荐登录：</span>
            <button className="wechat-button"><WechatOutlined /> 微信</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ModalLogin;
